package com.iyam.myvsgaproject2.ui.main.student;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageButton;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.iyam.myvsgaproject2.R;import com.iyam.myvsgaproject2.data.database.entity.StudentEntity;import java.util.ArrayList;import java.util.List;public class StudentAdapter extends RecyclerView.Adapter<StudentAdapter.StudentViewHolder> {    private List<StudentEntity> students = new ArrayList<>();    private OnItemClickListener listener;    @NonNull    @Override    public StudentViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View itemView = LayoutInflater.from(parent.getContext())                .inflate(R.layout.layout_student_item, parent, false);        return new StudentViewHolder(itemView);    }    @Override    public void onBindViewHolder(@NonNull StudentViewHolder holder, int position) {        StudentEntity currentStudent = students.get(position);        holder.bind(currentStudent);    }    @Override    public int getItemCount() {        return students.size();    }    public void setStudents(List<StudentEntity> students) {        this.students = students;        notifyDataSetChanged();    }    public interface OnItemClickListener {        void onDeleteClick(StudentEntity student);    }    public void setOnItemClickListener(OnItemClickListener listener) {        this.listener = listener;    }    public class StudentViewHolder extends RecyclerView.ViewHolder {        TextView tvStudentName, tvStudentId;        ImageButton btnDelete;        public StudentViewHolder(@NonNull View itemView) {            super(itemView);            tvStudentName = itemView.findViewById(R.id.tv_name);            tvStudentId = itemView.findViewById(R.id.tv_id_number);            btnDelete = itemView.findViewById(R.id.btn_delete);            btnDelete.setOnClickListener(v -> {                int position = getAdapterPosition();                if (listener != null && position != RecyclerView.NO_POSITION) {                    listener.onDeleteClick(students.get(position));                }            });        }        public void bind(StudentEntity student) {            tvStudentName.setText(student.getName());            tvStudentId.setText(String.valueOf(student.getIdNumber()));        }    }}